    try {
        // ØªØ­ÙˆÙŠÙ„ GeoJSON Ø¥Ù„Ù‰ Ø¥Ø­Ø¯Ø§Ø«ÙŠØ§Øª
        let geojson = JSON.parse(frm.doc.Ø§Ù„Ù…ÙˆÙ‚Ø¹_Ø§Ù„Ø®Ø§Øµ_Ø¨Ø§Ù„Ù…ÙƒØªØ¨);
