        let api_key = "YOUR_API_KEY"; // Ø¶Ø¹ Ù…ÙØªØ§Ø­ Google Maps Ø§Ù„Ø®Ø§Øµ Ø¨Ùƒ Ù‡Ù†Ø§
        let url = `https://maps.googleapis.com/maps/api/staticmap?center=${lat},${lng}&zoom=15&size=600x300&markers=color:red|${lat},${lng}&key=${api_key}`;

        // Ø¬Ù„Ø¨ Ø§Ù„ØµÙˆØ±Ø© ÙˆØªØ­ÙˆÙŠÙ„Ù‡Ø§ Ø¥Ù„Ù‰ Base64
        fetch(url)
            .then(response => response.blob())
