        // Ripple effect
        subCard.addEventListener('click', (e) => {
            const rect = subCard.getBoundingClientRect();
            const ripple = document.createElement('span');
            const size = Math.max(rect.width, rect.height);
            const x = e.clientX - rect.left - size / 2;
            const y = e.clientY - rect.top - size / 2;

            ripple.style.position = 'absolute';
            ripple.style.left = x + 'px';
            ripple.style.top = y + 'px';
            ripple.style.width = ripple.style.height = size + 'px';
            ripple.style.borderRadius = '50%';
            ripple.style.background = 'rgba(11,81,100,0.2)';
            ripple.style.transform = 'scale(0)';
            ripple.style.pointerEvents = 'none';
            ripple.style.transition = 'transform .6s ease, opacity .8s ease';

            subCard.appendChild(ripple);
            requestAnimationFrame(() => {
                ripple.style.transform = 'scale(1.8)';
                ripple.style.opacity = '0';
            });
            setTimeout(() => ripple.remove(), 800);
        });

        subGrid.appendChild(subCard);
    });

    mainCard.appendChild(subGrid);
    container.appendChild(mainCard);

})();

