/* ===== Workspace Container ===== */
.storage-theme .cards-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 20px;
    padding: 20px;
    justify-content: center;
    background: none;
}

/* ===== Main Card ===== */
.storage-theme .main-card {
    background: #ffffff;
    padding: 25px;
    border-radius: 18px;
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.08);
    display: flex;
    flex-direction: column;
    text-align: center;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

/* Hover Main Card */
.storage-theme .main-card:hover {
    transform: translateY(-6px) scale(1.02);
    box-shadow: 0 12px 28px rgba(0, 0, 0, 0.12);
}

/* Main Card Title */
.storage-theme .main-card h2 {
    font-size: 20px;
    font-weight: 700;
    margin-bottom: 20px;
    color: #0b5164;
    border-bottom: 2px solid #0b5164;
    padding-bottom: 8px;
    letter-spacing: 0.5px;
}

/* Sub Cards Container */
.storage-theme .sub-cards {
    display: flex;
    justify-content: space-between;
    gap: 16px;
    margin-top: 15px;
    width: 100%;
    flex-wrap: wrap;
}

/* Sub Card */
.storage-theme .sub-card {
    background: #f7f9fb;
    border-radius: 14px;
    padding: 12px;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    flex: 1;
    min-width: 0;
    position: relative;
    transition: transform 0.25s ease, box-shadow 0.25s ease, background 0.25s ease;
}

/* Hover Sub Card */
.storage-theme .sub-card:hover {
    transform: translateY(-3px) scale(1.03);
    box-shadow: 0 8px 18px rgba(0,0,0,0.1);
    background: #e6f0f5;
}

/* Icon */
.storage-theme .sub-card .icon-wrap {
    font-size: 24px;
    margin-bottom: 4px;
    color: #0b5164;
}

/* Label */
.storage-theme .sub-card span {
    font-size: 12px;
    font-weight: 600;
    color: #0b5164;
    text-align: center;
    line-height: 1.3;
}

/* Responsive */
@media (max-width: 992px) {
    .storage-theme .cards-grid {
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    }
}

@media (max-width: 480px) {
    .storage-theme .main-card {
        width: 95%;
    }
