css:
/* ================= إعداد الصفحة ================= */
@page {
  size: A4;
  margin: 15mm 15mm;
}

/* ================= التنسيقات العامة ================= */
.print-format {
  font-family: "Sultan Regular", "Sultan", "Tahoma", Arial, sans-serif;
  direction: rtl;
  text-align: right;
  font-size: 13px;
  line-height: 1.6;
  color: #000;
  background: #fff;
}

/* ================= رأس التقرير ================= */
.report-header {
  text-align: center;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-bottom: 2px solid #333;
  background-color: #f9f9f9;
  border-radius: 8px;
}

.report-header .title {
  font-family: "Sultan Bold", "Sultan", "Tahoma";
  font-size: 22px;
  font-weight: bold;
  color: #000;
  margin-bottom: 5px;
}

.report-header .sub-title {
  font-size: 14px;
  color: #333;
}

/* ================= العناوين الفرعية ================= */
.print-format h2, 
.print-format h3 {
  font-family: "Sultan Bold", "Sultan", "Tahoma";
  font-weight: bold;
  color: #000;
  page-break-after: avoid;
  break-after: avoid;
}

.print-format h2 {
  font-size: 16px;
  margin: 20px 0 10px;
  padding: 8px 10px;
  background: #eef2f5;
  border-right: 5px solid #2c3e50;
  border-radius: 4px;
}

.print-format h3 {
  font-size: 14px;
  margin: 15px 0 8px;
  padding-right: 8px;
  border-right: 3px solid #3498db;
}

/* ================= حاوية الأقسام لمنع التكسير ================= */
.section-container {
  page-break-inside: avoid;
}

/* ================= صناديق المعلومات ================= */
.info-box {
  margin-bottom: 15px;
  padding: 10px;
  background: #fff;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.info-box span {
  display: inline-block;
  min-width: 120px;
  font-weight: bold;
  color: #222;
}

/* ================= الجداول ================= */
.print-format table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 15px;
  background-color: #fff;
  table-layout: fixed;
}

.print-format th {
  background: #f0f0f0;
  border: 1px solid #999;
  padding: 6px;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
  color: #000;
  vertical-align: middle;
}

.print-format td {
  border: 1px solid #bbb;
  padding: 6px 8px;
  vertical-align: top;
  word-wrap: break-word;
  overflow-wrap: break-word;
}

thead { display: table-header-group; }
tr { page-break-inside: avoid; }

/* ================= التوصيات والملاحظات (إصلاح الترقيم) ================= */
.print-format .recommendations {
  margin-top: 10px;
  padding: 12px;
  background: #fbfbfb;
  border: 1px solid #ddd;
  border-right: 4px solid #27ae60;
  border-radius: 4px;
}

/* كود إصلاح القوائم والترقيم ليكون على اليمين */
.print-format .recommendations ol, 
.print-format .recommendations ul,
.print-format ol, 
.print-format ul {
  padding-right: 25px !important; /* مسافة للأرقام على اليمين */
  padding-left: 0 !important;     /* إلغاء مسافة اليسار */
  margin-right: 0;
  text-align: right;
  direction: rtl;
}

.print-format .recommendations li,
.print-format li {
  margin-bottom: 5px;
  line-height: 1.8; /* تحسين تباعد الأسطر داخل القائمة */
}
